import React from 'react';
import Link from 'next/link';
import useSWR from 'swr';

const fetcher = (...args) => fetch(...args).then(res => res.json())

const Home = () => {
  const { data, error } = useSWR('/api/get-promo', fetcher)

  return (
    <div>
      <p className='mt-12 text-center'>
        O restaurante X sempre busca por atender melhor seus clientes.<br />
        Por isso, estamos sempre abertos a ouvir a sua opinião.
      </p>

      <div className='text-center my-12 '>
        <Link href='/pesquisa'>
          <a className='bg-blue-400 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow'>Dar opinião ou sugestão</a>
        </Link>
      </div>

      {data && data.showCoupon ? 
        <p className='my-12 text-center'>{data.message}</p> : 
        <p className='my-12 text-center'>Carregando...</p>
      }

    </div>
  )
}

export default Home;
