import { GoogleSpreadsheet } from 'google-spreadsheet';
import credentials from '../../credentials.json';
import moment from 'moment';

const doc = new GoogleSpreadsheet('1g9vwNQMbh1YADO14sN_-0R7qFvF9wM3EBQJJHjs82Ug');

export default async (req, res) => {
  try {
    await doc.useServiceAccountAuth(credentials);
    await doc.loadInfo();

    const sheet = doc.sheetsByIndex[1];
    const data = JSON.parse(req.body);

    const sheetConfig = doc.sheetsByIndex[2];
    await sheetConfig.loadCells('A3:B3');

    const mostrarPromocaoCell = sheetConfig.getCell(2,0);
    const textoCell = sheetConfig.getCell(2,1);

    let Cupom = '';
    let Promo = '';

    if(mostrarPromocaoCell.value === 'VERDADEIRO'){
      Cupom = parseInt(moment().format('YYMMDDHHmmssSSS')).toString(16).toUpperCase();
      Promo = textoCell.value;
    }


    await sheet.addRow({
      Nome: data.Nome,
      Email: data.Email,
      WhatsApp: data.WhatsApp,
      'Data Preenchimento': moment().format('DD/MM/YYYY HH:mm:ss'),
      Nota: 5,
      Cupom,
      Promo
    })
  } catch (error) {
    res.end(JSON.stringify({
      showCoupon: false,
      message: 'error'
    }))
  }
}
