import React, { useState } from 'react';
import Link from 'next/link'

const Pesquisa = () => {
  const [form, setForm] = useState({
    Nome:'aaa',
    Email:'bbb',
    WhatsApp:'ccc'
  })

  const save = async () => {
    try {
      const response = await fetch('/api/save',{
        method:'POST',
        body: JSON.stringify(form)
      })

      const data = response.data;
      console.log(data)
    } catch(err){

    }
  }

  const onChange = evt => {
    const value = evt.target.value;
    const key = evt.target.name;

    setForm(old => ({
      ...old,
      [key]: value
    }))
  }
  return (
    <div className='pt-6'>
      <h1 className='text-center font-bold my-4 text-2xl'>Criticas e sugestões</h1>
      <p className='text-center mb-6'>
        O restaurante X sempre busca por atender melhor seus clientes.<br />
        Por isso, estamos sempre abertos a ouvir a sua opinião.
      </p>

      <div className='w-1/5 mx-auto'>
        <label className='font-bold'>Seu nome:</label>
        <input type='text' className='p-4 block shadow bg-blue-100 my-2 rounded' name='Nome' onChange={onChange} value={form.Nome}/>
        <button className='bg-blue-400 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow' onClick={save}>Salvar</button>
      </div>

    </div>
  )
}

export default Pesquisa;
